INSERT INTO `items_combos` (`combo_id`, `combo_name`, `id1`, `id2`, `id3`, `result`, `buff`, `type`, `chance`, `hook`, `hookcode`) VALUES (17, 'Suff', 'hsrcht', 'klfale', '', '', 0, 2, 150, '_codehook_', '// Item-Ergebnis kommt nicht zur Anwendung, dort können wir angeben was wir wollen\r\n\r\nglobal $session,$item_hook_info;\r\n\r\n$item_hook_info[''hookstop''] = true;\r\n\r\n$buff = array("name"=>"`1Besoffener Amoklauf`0", "rounds"=>50, "wearoff"=>"`1Du wirst allmählich wieder nüchtern!`0", "defmod"=>0.5,"atkmod"=>2, "roundmsg"=>"In deinem Suff prügelst du alles nieder was dir in den Weg kommt!", "activate"=>"defense,offense");\r\n\r\n$session[''bufflist''][''amok'']=$buff;\r\n\r\n$session[''user''][''drunkenness'']=90;\r\n\r\noutput(''`3Nach einigem wildem Herumgepansche bringst du eine etwas zähe Flüssigkeit zustande, die noch dazu bestialisch stinkt. Dennoch nimmst du todesmutig einen Schluck!`n`nAls du aus deiner Ohnmacht erwachst, verschwimmt alles vor deinen Augen. Doch bemerkenswerterweise hast du auf einmal eine immense Lust, irgendetwas zu zerstören! Da kommt dir die Keule gerade recht... '');\r\n\r\naddnews("`^Heute holten die Frauen schleunigst ihre Kinder ins Haus als `@".$session[''user''][''name'']."`^ in einem volltrunkenen Amoklauf die Stadt unsicher machte.`0");\r\n');
