INSERT INTO `masters` (`creatureid`, `creaturename`, `creaturelevel`, `creatureweapon`, `creaturelose`, `creaturewin`, `creaturegold`, `creatureexp`, `creaturehealth`, `creatureattack`, `creaturedefense`) VALUES (5, 'Unélith', 5, 'Gedankenkontrolle', 'Dein Geist ist stärker als meiner. Ich gebe auf.', 'Deine mentalen Kräfte haben noch Schwächen. Meditiere über dein Versagen und du wirst mich eines Tages besiegen können!', NULL, NULL, 55, 10, 10);
INSERT INTO `masters` (`creatureid`, `creaturename`, `creaturelevel`, `creatureweapon`, `creaturelose`, `creaturewin`, `creaturegold`, `creatureexp`, `creaturehealth`, `creatureattack`, `creaturedefense`) VALUES (6, 'Adwares', 6, 'Zwergische Kampfaxt', 'Ach!  Du führst dein %X mit Können!', 'Har! Du brauchst noch viel Übung!', NULL, NULL, 66, 12, 12);
INSERT INTO `masters` (`creatureid`, `creaturename`, `creaturelevel`, `creatureweapon`, `creaturelose`, `creaturewin`, `creaturegold`, `creatureexp`, `creaturehealth`, `creatureattack`, `creaturedefense`) VALUES (7, 'Gerrard', 7, 'Kampfbogen', 'Hmm, eventunnel hab ich dich unterschätzt.', 'Wie ich es mir gedacht habe.', NULL, NULL, 77, 14, 14);
INSERT INTO `masters` (`creatureid`, `creaturename`, `creaturelevel`, `creatureweapon`, `creaturelose`, `creaturewin`, `creaturegold`, `creatureexp`, `creaturehealth`, `creatureattack`, `creaturedefense`) VALUES (8, 'Ceiloth', 8, 'Orkos Breitschwert', 'Gut gemacht %W, Ich sehe grosse Taten in deiner Zukunft liegen.', 'Du wirst zwar mächtig, aber nicht so mächtig bis jetzt.', NULL, NULL, 88, 16, 16);
INSERT INTO `masters` (`creatureid`, `creaturename`, `creaturelevel`, `creatureweapon`, `creaturelose`, `creaturewin`, `creaturegold`, `creatureexp`, `creaturehealth`, `creatureattack`, `creaturedefense`) VALUES (9, 'Dwiredan', 9, 'Zwillingsschwerter', 'Vielleicht hätte ich etwas nehmen sollen wie dein %X...', 'Vielleicht solltest du mal über ein Zwillingsschwert nachdenken, bevor du es nochmal versuchst.', NULL, NULL, 99, 18, 18);
INSERT INTO `masters` (`creatureid`, `creaturename`, `creaturelevel`, `creatureweapon`, `creaturelose`, `creaturewin`, `creaturegold`, `creatureexp`, `creaturehealth`, `creatureattack`, `creaturedefense`) VALUES (10, 'Sensei Noetha', 10, 'Asiatische Kampfkünste', 'Dein Stil war überlegen, deine Verfassung besser. Ich verneige mich vor dir.', 'Lerne deinen Stil anzupassen und du könntest siegen.', NULL, NULL, 110, 20, 20);
INSERT INTO `masters` (`creatureid`, `creaturename`, `creaturelevel`, `creatureweapon`, `creaturelose`, `creaturewin`, `creaturegold`, `creatureexp`, `creaturehealth`, `creatureattack`, `creaturedefense`) VALUES (11, 'Celith', 11, 'Geworfene Halos', 'Wow, wie konntest du all diese Halos austricksen?', 'Vorsicht vor diesem letzten Halo, er kommt hierher zurück!', NULL, NULL, 121, 22, 22);
INSERT INTO `masters` (`creatureid`, `creaturename`, `creaturelevel`, `creatureweapon`, `creaturelose`, `creaturewin`, `creaturegold`, `creatureexp`, `creaturehealth`, `creatureattack`, `creaturedefense`) VALUES (12, 'Gadriel the Elven Ranger', 12, 'Elbischer Langbogen', 'Ich akzeptiere, dass du mich besiegt hast, weil Elfen unsterblich sind, du aber nicht. So ist der Sieg am Ende doch mein', 'Vergiss nicht, dass Elfen unsterblich sind. Sterbliche werden wahrscheinlich niemals einen von uns besiegen.', NULL, NULL, 132, 24, 24);
INSERT INTO `masters` (`creatureid`, `creaturename`, `creaturelevel`, `creatureweapon`, `creaturelose`, `creaturewin`, `creaturegold`, `creatureexp`, `creaturehealth`, `creatureattack`, `creaturedefense`) VALUES (13, 'Adoawyr', 13, 'Gargantuan Breitschwert', 'Wenn ich dieses Schwert hätte aufheben können, wäre ich wahrscheinlich besser gewesen.', 'Haha, ich konnte nichtmal das Schwert aufheben und hab trotzdem gewonnen!', NULL, NULL, 143, 26, 26);
