INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (1, '`8Pony`0', 'Es ist nicht unbedingt ein Schlachtross, außer natürlich man ist ein Halbling, aber noch immer günstig für ein Reittier. Treuer als der Esel und ausgesprochen gutmütig ist es alle Mal.', 'Reittiere', 'a:6:{s:4:"name";s:8:"`8Pony`0";s:8:"roundmsg";s:25:"Dein Pony kämpft mit dir!";s:9:"effectmsg";s:83:"`8Dein Pony schlägt wild aus und trifft deinen Gegner mit seinen Miniatur-Hufeisen.";s:6:"rounds";s:2:"20";s:6:"atkmod";s:3:"1.2";s:8:"activate";s:10:"roundstart";}', 4, 0, 1, 1, 1, 'Du steckst dein(e/n) {weapon} in die Satteltasche deines Ponys und machst dich auf ins Abenteuer!', '`&Remembering that is has been quite some time since you last fed your pony, you decide this is a perfect time to relax and allow it to graze the field a bit. You doze off enjoying this peaceful serenity.`0', '`&You dismount in the field to allow your pony to graze for a moment even though it has recently been fully fed.  As you lean back in the grass to watch the clouds, your pony whickers softly and trots off into the underbrush.  You search for a while before returning to the fields hoping that it''ll return.  A short time later, your pony trots back into the clearing holding its head high, looking much more energized and with a very equine grin on its face.`0', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (2, '`tWallach`0', 'Das typische Arbeitspferd. Kräftig und ausdauernd, auch wenn ein Ritt auf ihm eher weniger bequem ist. Man kommt damit zwar schneller voran als mit einem Pony, aber ein Rennpferd ist es noch lange nicht.', 'Reittiere', 'a:6:{s:4:"name";s:11:"`tWallach`0";s:8:"roundmsg";s:28:"Dein Wallach kämpft mit dir!";s:9:"effectmsg";s:66:"`tDein Wallach attackiert deinen Gegner mit Huftritten und Bissen.";s:6:"rounds";s:2:"40";s:6:"atkmod";s:3:"1.2";s:8:"activate";s:10:"roundstart";}', 6, 0, 1, 2, 1, 'Du schnallst dein {weapon} auf den Sattel deines Wallachs und machst dich auf ins Abenteuer!', '`&Dir fällt ein, dass es schon einen Weile her ist, seit du deinen Wallach zuletzt gefüttert hast. Deshalb beschliesst du, daß dies der richtige Augenblick ist, ihn hier grasen zu lassen und selbst etwas auszuspannen. Du döst vor dich hin und genießt die friedliche Umgebung.`0', '`&Du steigst ab und lässt deinen Wallach grasen, obwohl er erst gefüttert wurde. `0', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (3, '`THengst`0', 'Beliebt bei Boten und Kurieren ist der Hengst das günstigste, wirkliche Reitpferd. Zwar ist es noch immer kein Schlachtross, aber dafür edel, ausdauernd und schnell.', 'Reittiere', 'a:6:{s:4:"name";s:10:"`THengst`0";s:8:"roundmsg";s:27:"Dein Hengst kämpft mit dir!";s:9:"effectmsg";s:56:"`TDein Hengst schlägt aus und trifft deinen Gegner hart.";s:6:"rounds";s:2:"60";s:6:"atkmod";s:3:"1.2";s:8:"activate";s:10:"roundstart";}', 10, 0, 1, 3, 1, 'Du steckst dein {weapon} in die Satteltasche deines Hengst und machst dich auf ins Abenteuer.', '`&Dir fällt ein, dass es schon einen Weile her ist, seit du deinen Hengst zuletzt gefüttert hast. Deshalb beschliesst du, daß dies der richtige Augenblick ist, ihn hier grasen zu lassen und selbst etwas auszuspannen. Du döst vor dich hin und genießt die friedliche Umgebung.`0', '`&Du steigst ab und lässt deinen Hengst grasen, obwohl er erst gefüttert wurde.  Als du dich im Gras ausruhst, um die Wolken zu beobachten, trottet dein Hengst ins Unterholz. Du suchst ihn eine Weile, bevor du aufgibst und hoffst, dass er von alleine wiederkommt. Kurze Zeit später erscheint dein Hengst wieder auf der Lichtung, mit erhobenem Kopf und einem breiten Grinsen im Gesicht. Er sieht gut erholt aus`0', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (4, '`qHund`0', 'Der treuste Freund den ma für Geld kofen kann', 'Haustiere', 'a:10:{s:4:"name";s:8:"`qHund`0";s:8:"roundmsg";s:24:"Dein Hund kämpft mit dir";s:7:"wearoff";s:54:"Dein Hund ist erschöpft und kann nicht weiter kämpfen.";s:9:"effectmsg";s:46:"`qDein Hund greift mit gefletschten Zähnen an!";s:14:"effectnodmgmsg";s:39:"Dein Hund greift an, trifft aber nicht.";s:13:"effectfailmsg";s:22:"Dein Hund kratzt sich.";s:6:"rounds";s:2:"25";s:15:"minbadguydamage";s:1:"0";s:15:"maxbadguydamage";s:1:"5";s:8:"activate";s:7:"offense";}', 0, 1000, 1, 0, 0, 'Dein Hund streckt sich und ist bereit für neue Abentuer.', 'Dein Hund konnte sich vollständig erholen.', 'Dein Hund hat sich beim Herumschnüffeln etwas erholt.', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (5, '`vMaus`0', 'Ne Maus eben... tsss', 'Haustiere', 'a:7:{s:4:"name";s:11:"`vMäusezahn";s:7:"wearoff";s:56:"Deine Maus verkrümelt sich irgendwo in deiner Rüstung...";s:9:"effectmsg";s:33:"`vDeine Maus zwickt deinen Gegner";s:6:"rounds";s:2:"10";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:1:"2";s:8:"activate";s:7:"offense";}', 0, 100, 1, 0, 0, 'Du setzt dir deine Maus auf die Schulter und ziehst los.', 'Deine Maus hat sich völlig erholt.', 'Deine MAus konnte ein bisschen regenerieren.', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (6, '`VSchwarzer Drachen`0', 'Wahrscheinlich einer der am gefährlichsten wirkenden Drachlinge die ich im Angebot habe und mit ihren 6 Metern Spannweits auch die größten noch zähmbaren. Sie besitzen keinen gefährlichen Odem, sind aber ungewöhnlich geschickt im Kampf und ihre langen messerscharfen Zähne sind in allen Reichen gefürchtet. Die Farbe ihrer Schuppen reicht von hellem grau, bis hin zu tiefstem Schwarz.', 'Drachlinge', 'a:12:{s:4:"name";s:21:"`VSchwarzer Drachen`0";s:8:"roundmsg";s:25:"`7Dein Drachen greift an!";s:7:"wearoff";s:33:"`7Dein Drachen zieht sich zurück.";s:9:"effectmsg";s:52:"`VDein Drachen wirbelt deinen Gegner durch die Luft.";s:13:"effectfailmsg";s:38:"`7Dein Drachen verfehlt deinen Gegner.";s:6:"rounds";s:3:"250";s:6:"atkmod";s:3:"1.1";s:6:"defmod";s:3:"1.1";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:2:"15";s:12:"badguydmgmod";s:3:"0.9";s:8:"activate";s:7:"offense";}', 50, 200, 1, 3, 0, 'Dein schwarzer Drachen weckt dich früh ... begierig auf frisches Monsterfleisch.', 'Dein schwarzer Drachen setzt sich auf einen großen Baum am Rande der Lichtung und steckt den Kopf zwischen die Flügel. Nach seinem Nickerchen ist er völlig erholt.', 'Dein schwarzer Drachen setzt sich auf einen großen Baum am Rande der Lichtung und steckt den Kopf zwischen die Flügel. Sein lautes Schnarchen verscheucht sicher alle Monster in der Gegend. Naja, wenigstens konnte ER regenerieren.', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (7, '`gSchachtel Flöhe`0', 'Die heb ich selbst von meinen Kreaturen gepflückt. Nervige kleene Biester.', 'Haustiere', 'a:8:{s:4:"name";s:9:"`gFlöhe`0";s:8:"roundmsg";s:86:"Deine Flöhe lenken deinen Gegner ab und senken seinen Angriffs- und Verteidigungswert.";s:7:"wearoff";s:52:"Die Flöhe fallen erschöpft in ihre Schachtel zurück.";s:9:"effectmsg";s:47:"`gDie Flöhe hüpfen deinem Gegner in den Nacken.";s:6:"rounds";s:2:"25";s:12:"badguyatkmod";s:3:"0.9";s:12:"badguydefmod";s:3:"0.9";s:8:"activate";s:10:"roundstart";}', 1, 500, 1, 0, 0, 'Dir muss ein Floh entwischt sein. Ein Zwicken weckt dich früher als sonst.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (8, '`#Einhorn`0', 'Ein Prachtexemplar! Man kann auf ihm reiten und sein Horn hat heilende Wirkung. War nicht leicht zu zähmen!', 'Reittiere', 'a:7:{s:4:"name";s:11:"`#Einhorn`0";s:7:"wearoff";s:41:"Dein Einhorn zieht sich erschöpft zurück.";s:9:"effectmsg";s:88:"`#Dein Einhorn berührt dich mit seinem Horn und überträgt {damage} Lebenskraft auf dich.";s:14:"effectnodmgmsg";s:21:"Du bist völlig gesund";s:6:"rounds";s:3:"160";s:7:"lifetap";s:1:"1";s:8:"activate";s:10:"roundstart";}', 50, 200, 1, 3, 1, 'Dein Einhorn piekst dich mit seinem Horn und gibt dir frische Energie für den Tag.', 'Dein Einhorn stellt sich in den Schatten eines Baumes und grast. Du bemerkst, wie es weisslich schimmert, während es vollständig regeneriert.', 'Dein Einhorn verschwindet für eine Weile im Wald und kommt gut erholt zurück.', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (9, '`!Schattendrache`0', 'Rauch und Nebel. Keine greifbare Form und nichts als schemenhafte Erscheinungen um ihre zornigen und rot glühenden Augen. Eigentlich sind Schattendrachen keine wirklichen Drachen, sondern eine Art Dämon aus einer anderen Welt jenseits unserer Vorstellungskraft. Erst kurz vor ihrem todbringenden Biss, nehmen sie die feste Gestalt eines über 6 Meter langen, gewaltig wirkenden schwarzen Drachen ein.', 'Drachlinge', 'a:12:{s:4:"name";s:18:"`!Schattendrache`0";s:8:"roundmsg";s:94:"Der Schatten an Deiner Seite kämpft mit Dir. Du siehst die Furcht in den Augen Deines Feindes!";s:7:"wearoff";s:45:"Dein drache zieht sich zurück in die Schatten";s:9:"effectmsg";s:181:"`!Ein bedrohlicher Schatten baut sich hinter Deinem Gegner auf und ein hässliches Knacken ist zu hören, als sein Schrei verstummt - was für ein Glück, dass er an Deiner Seite kämpft";s:13:"effectfailmsg";s:79:"Der bedrohliche Schatten Deines Drachen verfehlt Deinen gegner um Haaresbreite.";s:6:"rounds";s:2:"30";s:6:"atkmod";s:3:"1.3";s:6:"defmod";s:3:"1.8";s:15:"minbadguydamage";s:1:"5";s:15:"maxbadguydamage";s:2:"30";s:12:"badguyatkmod";s:3:"0.5";s:8:"activate";s:7:"offense";}', 75, 15000, 1, 0, 0, 'Dein Schattendrache weckt dich früh ... begierig auf frisches Monsterfleisch.', 'Dein dunkler Gefährte ist zufrieden. Ein eiskalter Schauer fährt über Deinen Rücken und lässt Deine Haare zu Berge stehen - Wenn Du nicht wüsstest, dass es ein zeichen größter Dankbarkeit ist würdest Du am liebsten schreiend davon rennen', 'Dein dunkler Gefährte ist zufrieden.', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (10, '`6Schwarzes Kampfross`0', 'Genau das richtige Reittier für die großen Krieger unter euch. Seine Ausdauer ist nahezu unübertrefflich und seine Ausbildung bringt euch gute Vorteile in jedem Kampf.', 'Reittiere', 'a:12:{s:4:"name";s:23:"`6Schwarzes Kampfross`0";s:8:"roundmsg";s:25:"Dein Pferd kämpft mit Dir";s:7:"wearoff";s:34:"Erschöpft weicht Dein Pferd zurück";s:9:"effectmsg";s:87:"`6Dein Pferd bäumt sich auf und schlägt mit den Vorderhufe donnernd auf den Gegner ein.";s:13:"effectfailmsg";s:34:"Dein Pferd verfehlt seinen Gegner.";s:6:"rounds";s:3:"100";s:6:"atkmod";s:3:"1.1";s:6:"defmod";s:3:"1.2";s:15:"minbadguydamage";s:1:"3";s:15:"maxbadguydamage";s:1:"8";s:12:"badguyatkmod";s:3:"0.9";s:8:"activate";s:10:"roundstart";}', 20, 3000, 1, 2, 1, 'Du hörst ein Scharren von Hufen. Dein Pferd ist also bereits wach und bereit für einen neuen Tag!', 'Liebevoll stubbst Dich das riesige Tier mit der Schnauze an.', 'Ein dankbares Schnauben prickelt auf Deiner Haut', 30, 50, 50, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (11, '`1Specter`0', 'Geisterhafter Schemen aus den Tiefen des verlassenen Schlosses herauf beschworen. Sie lassen sich nicht lange in unserer Welt halten, wodurch ihre Kräfte schnell schwinden. Trotzdem ist allein ihr Anblick bereits für viele Gegner Grund genug, das Weite zu suchen.', 'Geister und Dämonen', 'a:11:{s:4:"name";s:11:"`1Specter`0";s:8:"roundmsg";s:97:"Eine Geisterhand legt sich auf den Nacken Deines Feindes und lässt ihm die Haare zu Berge stehen.";s:7:"wearoff";s:32:"Dein Feind beruhigt sich wieder!";s:9:"effectmsg";s:89:"`1Dein Gegner schaut sich gehetzt um, als wenn ihn eine dunkle Erinnerung verfolgen würde";s:13:"effectfailmsg";s:40:"Dein Gegner hat keine Angst vor Geistern";s:6:"rounds";s:2:"10";s:6:"defmod";s:3:"0.6";s:12:"badguydmgmod";s:3:"0.7";s:12:"badguyatkmod";s:3:"0.9";s:12:"badguydefmod";s:3:"0.7";s:8:"activate";s:10:"roundstart";}', 10, 0, 1, -2, 0, 'Ein kalter, übelriechender Hauch streift Dein Gesicht. Du öffnest die Augen. Dass hässlich durchscheinende Gesicht des Geistes dicht über Deinem! `#"Ich grüße Euuch mein Meiiister...."', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (12, '`4P`$h`qo`6e`^n`7i`&x`0', 'Also ich werd nicht schlau aus dem Vieh.', 'Magische Wesen', 'a:9:{s:4:"name";s:23:"`4P`$h`qo`6e`^n`7i`&x`0";s:7:"wearoff";s:84:"Dein Phoenix scheint durch irgendwas im Wald abgelenkt zu werden und flattert davon.";s:9:"effectmsg";s:44:"Dein Phoenix flattert wirr um deinen Gegner.";s:14:"effectnodmgmsg";s:39:"Dein Phoenix spielt mit einem Grashalm.";s:13:"effectfailmsg";s:60:"Nur knapp entkommt dein Phoenix einem Schlag deines Gegners.";s:6:"rounds";s:2:"80";s:5:"regen";s:1:"1";s:7:"lifetap";s:3:"1.1";s:8:"activate";s:7:"defense";}', 25, 8000, 1, -1, 0, 'Dein Phoenix hat dich die ganze Nacht kaum Schlafen lassen. Völlig fertig hast du heute einen Waldkampf weniger.', 'Dein Phoenix hat sich prächtig amüsiert und regeneriert vollständig.', 'Dein Phoenix amüsiert sich gut und regeneriert etwas.', 0, 0, 0, '', '', '', 0, 1.14);
