INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (14, '`2Wyver`0', 'Eine gut 3 Meter lange, schlangenähnliche Kreatur. Knapp einen Meter unterhalb des Kopfes, der mehr dem eines Drachen ähnelt, besitzt die Wyver kleine, kaum brauchbare Flügel, welche meist eng an ihren schlanken Körper angelegt sind.', 'Drachlinge', 'a:10:{s:4:"name";s:9:"`2Wyver`0";s:8:"roundmsg";s:26:"Deine Wyver kämpft mit dir";s:7:"wearoff";s:34:"Erschöpft fliegt deine Wyver davon";s:9:"effectmsg";s:43:"`2Dein Wyver stürzt sich auf deinen Gegner.";s:6:"rounds";s:3:"180";s:6:"atkmod";s:4:"1.05";s:6:"defmod";s:1:"1";s:15:"minbadguydamage";s:1:"5";s:15:"maxbadguydamage";s:2:"20";s:8:"activate";s:7:"offense";}', 10, 5000, 1, 3, 0, 'Kampfbereit flattert deine Wyver mit den Flügeln', 'Nach der Rast fühlt sich deine Wyver erholt und ist wieder kampfbereit', 'Nach der kurzen Rast hat deine Wyver wieder Lust auf ein paar gute Kämpfe', 0, 0, 0, '', '', '', 0, 1.14);
