INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (16, '`$Skelettkrieger`0', 'Mit Hilfe einer kurzen Zauberformel aus einer Handvoll alter Knochen beschworen, folgt euch der Skelettkrieger ohne zu fragen bis in den Tod. Sehr flink, aber dafür mit nicht einmal dem gerinsten Maß an Intelligenz gesegnet, sind sie nicht unbedingt die besten, aber dafür relativ günstige Gefährten.', 'Geister und Dämonen', 'a:16:{s:4:"name";s:18:"`$Skelettkrieger`0";s:8:"roundmsg";s:50:"`$Deine Untoten Diener stürzen sich auf den Feind!";s:7:"wearoff";s:43:"`$Dein Skelett zerfällt in etwa 200 Teile..";s:9:"effectmsg";s:41:"`$Dein Skelett stürzt sich auf den Gegner";s:14:"effectnodmgmsg";s:45:"`$Dein Skelett läuft im Kreis um den Gegner..";s:13:"effectfailmsg";s:46:"`$Das Skelett fängt an auf dich einzuschlagen!";s:6:"rounds";s:2:"80";s:6:"atkmod";s:3:"1.1";s:6:"defmod";s:3:"0.8";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"0";s:15:"maxbadguydamage";s:2:"50";s:7:"lifetap";s:1:"0";s:12:"badguyatkmod";s:3:"0.9";s:12:"badguydefmod";s:4:"0.75";s:8:"activate";s:7:"offense";}', 12, 1000, 1, 0, 0, '`$Mit klappernden Knochen, erhebt sich dein untoter Diener.. bereit erneut für dich zu sterben.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
