INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (18, '`4Balrog`0', 'Uii,det is''n Feuerdämon,uralt,und immer schlecht gelaunt,sach ich dir.Hat mich ganz schön viel Brandsalbe gekostet der Junge.', 'Mod-Biester', 'a:8:{s:4:"name";s:10:"`4Balrog`0";s:8:"roundmsg";s:100:"`4Die ganze Umgebung scheint in Flammen gehüllt und die unglaubliche Hitze lässt deine Haut erröten!";s:7:"wearoff";s:91:"`4Die Flammen und sein Gebieter ziehen sich zurück in die lodernden Feuer seiner Behausung.";s:6:"rounds";s:2:"90";s:6:"atkmod";s:3:"1.5";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:2:"50";s:8:"activate";s:7:"offense";}', 50000, 50000, 0, 0, 0, '`4Du verspürst eine ungewöhnliche Hitze in deiner Nähe.Als du die Augen öffnest steht dein Balrog direkt vor dir, heiß darauf, deine Gegner zu peinigen.', '`4Dein Balrog setzt sich auf einen Stein und schließt die Augen,die Flammen auf seinem Körper beginnen,niedriger zu züngeln.Als er die Augen wieder öffnet,scheint sein Feuer wieder mit voller Kraft zu lodern.', '`4Dein Balrog setzt sich auf einen Stein,doch statt sich auszuruhen,macht er sich einen Spaß daraus,alles in seiner Reichweite anzukokeln.Dennoch wirkt er irgendwie erfrischt,als ihr wieder aufbrecht.', 30, 50, 20, '`4Dein Balrog wartet desinteressiert am Höhleneingang auf dich.', '`4Dein Balrog verwandelt deine Leiche zu Asche und sieht sich nach etwas Anderem um,dem er Furcht einflößen kann.', '`4Dein Balrog zieht dich aus aus der einstürzenden Mine heraus und beachtet dich dann nicht weiter.', 200, 1.14);
