INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (20, '`9Cerapter`0', 'Ein Geflügeltes Einhorn.. etwas ganz seltenes!', 'Reittiere', 'a:10:{s:4:"name";s:12:"`9Cerapter`0";s:7:"wearoff";s:84:"`9Dein Cerapter ist erschöpft und nimmt einige Flügelschläge weit Abstand vom Kampf.";s:9:"effectmsg";s:37:"`9Dein Cerapter greift den Gegner an!";s:6:"rounds";s:3:"150";s:6:"atkmod";s:3:"1.2";s:5:"regen";s:1:"1";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:1:"9";s:7:"lifetap";s:3:"0.5";s:8:"activate";s:10:"roundstart";}', 75, 15000, 1, 3, 1, 'Dein Cerapter begrüßt dich mit temperamentvollem Flügelschlagen und Schnauben - bereit, in den Kampf zu ziehen.', 'Dein erschöpftes Cerapter rupft eifrig Gras und legt sich dann eine Weile mit ausgestreckten Flügeln in die Sonne. Nach einer Weile ist es wieder vollkommen bei Kräften.', 'Dein Cerapter streift ruhig über die Lichtung und frißt hier und da etwas Gras, um sich zu stärken.', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (21, '`7Katze`0', 'Ein niedlicher Schoßtiger.. aber kann ganz schön kratzbürstig werden.', 'Haustiere', 'a:8:{s:4:"name";s:9:"`7Katze`0";s:7:"wearoff";s:135:"`7Deine Katze findet, daß sie genug gekämpft hat und es Zeit für eine Mahlzeit wird. Sie legt sich in der Nähe nieder und sieht dir zu.";s:9:"effectmsg";s:58:"`7Deine Katze versucht, dem Gegner die Augen auszukratzen.";s:6:"rounds";s:2:"25";s:15:"minbadguydamage";s:1:"0";s:15:"maxbadguydamage";s:1:"3";s:12:"badguyatkmod";s:3:"0.7";s:8:"activate";s:7:"offense";}', 0, 500, 1, 0, 0, 'Deine Katze begrüßt dich mit lautem Schnurren und streift dir um die Beine. Dir ist klar, daß sie keine Tatze rühren wird im Kampf, wenn ihr Magen leer ist, also füllst du deinem kleinen Pascha die Futterschale.', 'Deine Katze läßt sich matt ins Gras fallen und schläft den Schlaf der Gerechten. Als sie aufwacht ist sie wieder quietschfidel.', 'Deine Katze spielt eine Weile mit einem Schmetterling und legt sich dann auf einen warmen Fels, um zu dösen.', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (22, '`$T`4hestral`0', 'Hast du den Tod gesehen? So siehe dem Thestral ins Auge.', 'Mod-Biester', 'a:11:{s:4:"name";s:14:"`$T`4hestral`0";s:8:"roundmsg";s:40:"`4Dein Thestral umkreist dich schützend.";s:7:"wearoff";s:47:"`4Dein Thestral verschwindet in den Schatten.`0";s:9:"effectmsg";s:67:"`4Dein Thestral berührt dich, worauf deine Wunden sich schließen.`0";s:13:"effectfailmsg";s:71:"`4Dein Thestral stellt sich schützend vor dich und spannt die Flügel.`0";s:6:"rounds";s:3:"300";s:6:"atkmod";s:3:"1.6";s:6:"defmod";s:3:"1.6";s:5:"regen";s:1:"6";s:7:"lifetap";s:1:"1";s:8:"activate";s:26:"roundstart,offense,defense";}', 2000, 300000, 0, 3, 1, '`4Dein Thestral versteckt sich in den Schatten deines Anwesens, funkelt dich mit seinen roten Augen an und wartet still und unbemerkt.', 'Dein Thestral verschwindet und kommt kurz darauf gestärkt wieder.', '', 100, 0, 75, '`4Dein Thestral flog dich vor die Miene, was sich wohl ausser Gold darin befindet?`0', '`4Dein Thestral versuchte dich zu retten, erfolglos. Es verschwindet zurück in die Schatten.`0', '`4Dein Thestral hat sich für dich geopfert und dich gerettet.`0', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (23, '`QHippogryph`0', 'Teils Vogel, teils Pferd! Schickes ding!', 'Reittiere', 'a:8:{s:4:"name";s:14:"`QHippogryph`0";s:7:"wearoff";s:68:"`QDein Hippogreif sackt erschöpft zusammen, bevor es davon flattert.";s:9:"effectmsg";s:56:"`QDein Hippogreif ermutigt dich für den nächsten Angriff";s:13:"effectfailmsg";s:36:"`QDein Hippogreif spannt die Flügel.";s:6:"rounds";s:3:"100";s:6:"atkmod";s:3:"1.1";s:6:"defmod";s:3:"1.1";s:8:"activate";s:10:"roundstart";}', 40, 10000, 1, 3, 1, '`QDein Hippogreif weckt dich mit einem Krähen sanft aus deinen Träumen.', '`QDein Hippogreif verschwindet kurz zwischen den Bäumen, ehe es gestärkt zurückkehrt.', '', 0, 0, 0, '', '', '', 0, 1.14);
