INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (28, '`tGwaihir`0', 'Ein Riesenadler, einer der Windfürsten, mehr gibbet nicht zu sachn mein Freund. Dat Ding fliecht besser als jeder Drache!', 'Vögel', 'a:13:{s:4:"name";s:11:"`tGwaihir`0";s:8:"roundmsg";s:52:"`tDein Gwaihir fliegt bedrohlich über deinem Gegner.";s:7:"wearoff";s:57:"`tDein Gwaihir landet erschöpft hinter dir, kampfunfähig.";s:9:"effectmsg";s:90:"`tDein Gwaihir gibt ein bedrohliches Geräusch von sich, was deine Aufmerksamkeit steigert.";s:13:"effectfailmsg";s:50:"`tDein Gwaihir schlägt beunruhigt mit den Flügeln.";s:6:"rounds";s:3:"120";s:6:"atkmod";s:3:"1.1";s:6:"defmod";s:3:"1.3";s:5:"regen";s:1:"4";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:2:"10";s:12:"badguyatkmod";s:3:"0.9";s:8:"activate";s:7:"offense";}', 175, 10000, 0, 4, 0, '`tDein Gwaihir fliegt über dem Anwesen, wartet nur darauf, das du hervor kommst.', '`tDein Windfürst fliegt über die Wälder, so schnell wie der Blitz, verschwindet es auch schon, doch ebenso schnell kommt es gestärkt zurück.', '`tDein Gwaihir rasst über die Felder, auf der suche nach ein bisschen Stärkung.', 100, 0, 100, '`tDein Gwaihir landet sanft vor der Miene. Was es da drin wohl gibt?', '', '`tDein Gwaihir wittert die Gefahr und schafft dich raus, bevor es überhaupt angefangen hat gefährlich zu werden!', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (29, '`4Vampirfledermäuse`0', 'Mistige Viecher! Beissen sofort zu wenn man ihnen zu nah kommt!', 'Magische Wesen', 'a:7:{s:4:"name";s:21:"`4Vampirfledermäuse`0";s:8:"roundmsg";s:45:"`4Deine Fledermäuse flattern aufgerget umher.";s:7:"wearoff";s:83:"`4Deine Fledermäuse hängen sich an einen Baum und lassen dich allein weiterkämpfen.";s:9:"effectmsg";s:90:"`4Die Vampirfledermäuse entziehen deinem Gegner Lebenskraft und übertragen diese auf dich!";s:6:"rounds";s:2:"40";s:7:"lifetap";s:1:"1";s:8:"activate";s:7:"offense";}', 25, 1000, 1, 0, 0, '`4Das Flattern deiner Fledermäuse verkündet dir  einen neuen Tag.', '`4Deine Fledermäuse hängen sich an eine fette, glückliche Kuh, die gerade die saftigen Gräser kaut, und saugen sich an ihr voll.', '`4Deine Fledermäuse jagen sich ein paar kleine Säugetiere und kommen dann halbwegs erholt zu dir zurück', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (30, '`6Harpye`0', 'Teils Vogel, teils Frau. Hinterhältig und sehr eigensinnig, wie Frauen nunmal sind! Nimm dich in Acht! Ich traue dem Biest kein Stück!', 'Magische Wesen', 'a:14:{s:4:"name";s:10:"`6Harpye`0";s:7:"wearoff";s:31:"Deine Harpye lässt dich allein.";s:9:"effectmsg";s:134:"Deine Harpye lässt eine Salve ihrer rasiermesserscharfen Federn auf deinen Gegner niedersausen und trifft für {damage} Schadenspunkte.";s:14:"effectnodmgmsg";s:40:"Deine Harpye stachelt dich zum Kampf an.";s:13:"effectfailmsg";s:73:"Deine Harpye kreist über dir und deinem Gegner und verspottet Euch beide.";s:6:"rounds";s:2:"80";s:6:"atkmod";s:4:"1.05";s:6:"defmod";s:3:"0.9";s:11:"minioncount";s:1:"3";s:15:"minbadguydamage";s:1:"2";s:15:"maxbadguydamage";s:1:"7";s:12:"badguyatkmod";s:3:"1.2";s:12:"badguydefmod";s:3:"0.8";s:8:"activate";s:10:"roundstart";}', 10, 2000, 1, -4, 0, 'Der neue Tag beginnt damit, dass du erstmal deine Harpye einfangen musst, die mal wieder ausgebüxt ist und sich weigert dir zu folgen. Dadurch verlierst du 4 Waldkämpfe!', 'Deine Harpye ist glücklich vollgefressen und spielt mit dem Gedanken einmal das zu tun was du von ihr willst.', 'Deine Harpye isst ein wenig und schaut dich leicht verächtlich an.', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (31, '`tGolem`0', 'Ein mächtiger Krieger aus Lehm und Gestein. Nicht sehr Klug, aber wo der hinhaut möcht ich nicht sein.', 'Magische Wesen', 'a:9:{s:4:"name";s:9:"`tGolem`0";s:8:"roundmsg";s:55:"`tDein Golem schlägt und stampft auf deinen Gegner ein!";s:7:"wearoff";s:49:"`tDein Golem rollt sich zu einem Felsen zusammen.";s:13:"effectfailmsg";s:54:"`tDein Golem packt und quetscht den nächstbesten Baum.";s:6:"rounds";s:2:"50";s:6:"atkmod";s:3:"1.2";s:6:"defmod";s:3:"1.1";s:12:"badguydefmod";s:3:"0.9";s:8:"activate";s:7:"offense";}', 30, 5000, 1, -1, 0, 'Als du erwachst, steht dein Golem noch genau da wo du ihn verlassen hast.', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (32, '`^Goldener Skarabäus`0', 'Wad det soll weiß ich nicht... Hat sich die letzten Tage kein Stück bewegt... billig wirds aber trotzdem nicht!', 'Magische Wesen', 'a:5:{s:4:"name";s:22:"`^Goldener Skarabäus`0";s:9:"effectmsg";s:72:"Der Skarabäus strahlt in hellem Schein und deine Wunden schliessen sich!";s:6:"rounds";s:2:"50";s:5:"regen";s:1:"5";s:8:"activate";s:10:"roundstart";}', 30, 0, 1, -1, 0, 'Auch heute morgen hat sich dein Skarabäus noch kein Stück bewegt. In der Hoffnung ihn zu irgendetwas motivieren zu können opferst du einen Waldkampf.', 'Der Skarabäus leuchtet wieder in strahlendem Gold.', 'Der Skarabäus leuchtet in schwachem Gold.', 100, 0, 100, '', '', 'Das goldene Licht des Skarabäus weist dir rechtzeitig den Weg nach draussen - hast du ein Glück!', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (33, '`^Goldfisch im Glas`0', 'Ja dees is ein feiner Bursche! Wenn alles Andere für dich zu teuer ist...', 'Haustiere', 'a:8:{s:4:"name";s:26:"&#180;^Goldfisch im Glas`0";s:8:"roundmsg";s:52:"`^Dein Goldfisch schwimmt in seinem Glas hin und her";s:7:"wearoff";s:25:"`^Dein Goldfisch schläft.";s:9:"effectmsg";s:53:"`^Dein Goldfisch schwimmt in seinem Glas hin und her.";s:14:"effectnodmgmsg";s:53:"`^Dein Goldfisch schwimmt in seinem Glas hin und her.";s:13:"effectfailmsg";s:24:"`^Dein Goldfisch blubbt.";s:6:"rounds";s:3:"300";s:8:"activate";s:10:"roundstart";}', 0, 10, 1, 0, 0, '`^Dein Goldfisch schwimmt in seinem Glas hin und her', '`^Dein Goldfisch schwimmt in seinem Glas hin und her', '`^Dein Goldfisch schwimmt in seinem Glas hin und her', 0, 0, 0, '', '`^Zu dumm dass Goldfische keine Höhlentiere sind', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (34, '`TSchwarzer Panther`0', 'Ahhhh! Ein elegantes und edles Tier! Aber teuer ist es. Sehr teuer!', 'Wildtiere', 'a:12:{s:4:"name";s:21:"`TSchwarzer Panther`0";s:8:"roundmsg";s:40:"`TDein Panther springt deinen Gegner an.";s:7:"wearoff";s:30:"`TDein Panther ist außer Atem.";s:9:"effectmsg";s:55:"`TDein Panther trifft deinen Gegner mit seinen Pranken.";s:13:"effectfailmsg";s:24:"`TDein Panther verfehlt!";s:6:"rounds";s:2:"90";s:6:"atkmod";s:4:"1.15";s:6:"defmod";s:4:"1.15";s:15:"minbadguydamage";s:2:"10";s:15:"maxbadguydamage";s:2:"15";s:12:"badguyatkmod";s:3:"0.9";s:8:"activate";s:7:"offense";}', 60, 3000, 1, 3, 0, 'Dein Panther weckt dich in aller Früh, hungrig und kampfeslustig.', 'Dein Panther ist nun gestärkt und wieder voll einsatzfähig.', 'Dein Panther frisst ein wenig und kommt dann zu dir zurück.', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (35, '`4Erz`Qtro`^ll`0', '`2Der gewöhnliche `4Erz`Qtro`^ll`2 ist eine äußerst furchterregende Erscheinung.', 'Mod-Biester', 'a:14:{s:4:"name";s:8:"Erztroll";s:8:"roundmsg";s:61:"`2Keine Angst, dein `4Erz`Qtro`^ll`2 steht direkt hinter dir.";s:7:"wearoff";s:53:"`2Dein `4Erz`Qtro`^ll`2 verschwindet in seiner Höhle.";s:9:"effectmsg";s:72:"`2Dein `4Erz`Qtro`^ll`2 wirbelt seine Keule und trifft den Gegner schwer";s:14:"effectnodmgmsg";s:70:"`2Dein `4Erz`Qtro`^ll`2 wirbelt seine Keule.. in die falsche Richtung.";s:13:"effectfailmsg";s:70:"`2Dein `4Erz`Qtro`^ll`2 wirbelt seine Keule.. in die falsche Richtung.";s:6:"rounds";s:3:"120";s:6:"atkmod";s:3:"1.2";s:6:"defmod";s:3:"1.1";s:5:"regen";s:1:"1";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:2:"10";s:7:"lifetap";s:1:"1";s:8:"activate";s:15:"offense,defense";}', 10000, 75, 0, 0, 1, '`2Dein `4Erz`Qtro`^ll`2 gibt ein Rülpsen von sich und tapst hinter dir her.', '`2Dein `4Erz`Qtro`^ll`2 reibt sich zufrieden den gigantischen Bauch.', '`2Dein `4Erz`Qtro`^ll`2 hat den halben Wald von Rehen befreit, ist aber immer noch nicht satt.', 100, 0, 100, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (36, '`tRennschwein`0', 'Ay... pack die Leine und halt sie gut fest, das Ding geht ab wie... die Wutz halt...', 'Reittiere', 'a:7:{s:4:"name";s:15:"`tRennschwein`0";s:8:"roundmsg";s:77:"Dein Rennschwein saust durch die Gegend und zieht dich an der Leine hinterher";s:6:"rounds";s:2:"80";s:6:"atkmod";s:3:"0.8";s:6:"defmod";s:3:"0.8";s:12:"badguyatkmod";s:3:"0.5";s:8:"activate";s:10:"roundstart";}', 10, 1000, 1, 10, 1, '', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (37, 'Baumdrache', 'Baumdrachen sind eine kleine und weit weniger intelligente Version der großen grünen Drachen. Ihr Feueratem ist nicht wirklich beeindruckend, aber auf kurze Distanz dennoch sehr schmerzhaft. Baumdrachen erreichen eine Spannweit von bis zu 3 Metern.', 'Drachlinge', 'a:5:{s:6:"rounds";s:2:"90";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"5";s:15:"maxbadguydamage";s:2:"15";s:8:"activate";s:7:"offense";}', 20, 2500, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (38, 'Perldrache', 'Wahrscheinlich einer der elegantesten Drachen im ganzen Kaiserreich. Ihr Körper erreicht eine Länge von annähernd 5 Metern und ist beinahe so schlank wie der einer Wyver. In ihrer Heimat an den westlichen Küsten, stürzen sie sich aus großer Höhe in die Fluten um ihre Lieblingsnahrung, frischen Fisch zu jagen, wobei sie sich unter Wasser nicht minder elegant bewegen, wie in der Luft.', 'Drachlinge', 'a:5:{s:6:"rounds";s:3:"120";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"8";s:15:"maxbadguydamage";s:2:"12";s:8:"activate";s:7:"offense";}', 30, 5000, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (39, 'Eisdrache', 'Eigentlich mehr ein Drachling und kein voll ausgewachsener Drache. Der eisige Atem dieser bis zu 5 Meter langen, silbrig schimmernden Kreaturen ist aber dennoch absolut tödlich für all jene, welche sich zu nahe an sie heran wagen.', 'Drachlinge', 'a:5:{s:6:"rounds";s:3:"160";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:2:"10";s:15:"maxbadguydamage";s:2:"20";s:8:"activate";s:7:"offense";}', 75, 10000, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 15, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (40, 'Roter Drache', 'Ebenfalls nur ein Drachling. Eine kleinere Version ihrer großen Verwandten aus dem fernen Süden, deren feuriger Atem ganze Landstriche verheeren konnte. Diese Exemplare hier, sind mit ihren 5-6 Metern Spannweite noch lange nicht ausgewachsen, doch kann ihr Atem dennoch jeden Gegner ohne große Probleme garen lassen.', 'Drachlinge', 'a:5:{s:6:"rounds";s:3:"180";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"8";s:15:"maxbadguydamage";s:2:"18";s:8:"activate";s:7:"offense";}', 75, 10000, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 15, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (41, 'Ghul', 'Jene unter euch, denen Ästhetik eher weniger wichtig ist, mögen den Ghul einem Skelettkrieger vorziehen. Sie sind ausdauernder und kräftiger, doch ist der stinkende und permanent verwesende, ehemals humanoide Körper nicht für jeden zu ertragen.', 'Geister und Dämonen', 'a:7:{s:6:"rounds";s:3:"100";s:6:"defmod";s:3:"0.9";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"2";s:15:"maxbadguydamage";s:1:"8";s:12:"badguyatkmod";s:3:"0.9";s:8:"activate";s:7:"offense";}', 0, 2000, 0, -1, 0, '', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (42, 'Succubus', 'Gebt auf euch Acht, solltet ihr euch wahrhaftig eines dieser verderbten und diabolischen Wesen erwerben wollen. Sie sind zwar sehr gute und ausdauernde Kämpfer, doch soll schon so mancher Möchtegern-Abenteurer ihnen in tiefer Nacht erlegen sein. Nicht ihren schaften Klingen, sondern ihrer unbeschreiblichen Schönheit. Letztenendes, führt aber beides bei Succubi zum Tode.', 'Geister und Dämonen', 'a:7:{s:6:"rounds";s:3:"120";s:6:"defmod";s:3:"0.9";s:11:"minioncount";s:1:"2";s:15:"minbadguydamage";s:2:"10";s:15:"maxbadguydamage";s:2:"15";s:12:"badguyatkmod";s:3:"0.8";s:8:"activate";s:7:"offense";}', 40, 10000, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 10, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (43, 'Kobold', 'Ein Exemplar auf das ich besonders stolz bin. Kobolde sind noch etwas kleiner aus Goblins und werden vielleicht einen halben Meter groß. Aber diese hier, sind nicht nur einfache Kobolde, sondern ihre magisch begabten Verwandten aus den Unterwelten. Sie können eure Gegner verfluchen und selbst aus geschickten Kriegern einen Tollpatsch machen.', 'Geister und Dämonen', 'a:5:{s:6:"rounds";s:2:"20";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:3:"0.5";s:12:"badguydefmod";s:3:"0.9";s:8:"activate";s:10:"roundstart";}', 20, 1000, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (44, 'Pixie', 'Auch oft Waldfee genannt. Handgroße, humanoid erscheinende Kreaturen mit libellenartigen Flügeln. Sie sind weder gefährlich, noch in irgendeiner Weise aggressiv. Merricks Exemplare können zwar nicht sprechen, doch besitzen sie die gleichen magischen Fähigkeiten und die gleiche, alles regenerierende Aura, wie ihre Feenbrüder und Schwestern aus den Wäldern um Atrahor.', 'Magische Wesen', 'a:4:{s:6:"rounds";s:2:"80";s:5:"regen";s:1:"1";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 30, 5000, 0, 1, 0, '', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (45, 'Banshee', 'Die Banshee, oder auch Todesfee, tritt in Form einer alten, verwelkten Elfe mit eisig blasser Haut in Erscheinung. Beständig  scheint sie zu jammern und zu klagen, aber erst bei der Gefahr eines nahenden Gegners beginnt sie ihr wahres, Mark und Bein durchdringendes Klagelied, welches selbst gestandenen Helden den Mut auf alle Hoffnung zu nehmen vermag.', 'Geister und Dämonen', 'a:7:{s:6:"rounds";s:2:"30";s:6:"atkmod";s:4:"0.95";s:6:"defmod";s:4:"0.95";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:3:"0.5";s:12:"badguydefmod";s:4:"0.95";s:8:"activate";s:10:"roundstart";}', 35, 7500, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (46, 'Kröte', 'Warzige kleine Viecher, die Merrick einem örtlichen Hexenzirkel abschwatzen konnte. Viel gibt es zu diesen faustgroßen Tieren nicht zu sagen. Haltet sie stets feucht, denn ihre Sekrete verursachen nicht nur starken Juckreiz, sondern vermögen auch kleinere Wunden zu heilen.', 'Haustiere', 'a:3:{s:6:"rounds";s:2:"10";s:5:"regen";s:1:"1";s:8:"activate";s:10:"roundstart";}', 1, 50, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (47, 'Papagei', 'Ein bunter Vogel mit großem Schnabel...leider aber auch einem großen Mundwerk. Früher einmal von Piraten an den westlichen Küsten gehalten, kennt dieses Tier Schimpfworte und Begriffe, die noch jedem Schamesröte ins Gesicht treiben konnten. Merrick wird dir einen besonders guten Preis machen müssen...', 'Haustiere', 'a:4:{s:6:"rounds";s:2:"50";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:4:"0.85";s:8:"activate";s:10:"roundstart";}', 5, 500, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (48, 'Frettchen', 'Klein, niedlich und hübsch anzusehen, liegen die Vorteile des Frettchens in seiner Statur. Es gibt keine Rüstung welche derart lückenlos ist, als dass nicht ein Frettchen noch hinein schlüpfen und einen wunden Punkt finden könnte...und ihre Bisse sind alles andere als angenehm.', 'Haustiere', 'a:5:{s:6:"rounds";s:2:"30";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:1:"4";s:8:"activate";s:7:"offense";}', 0, 200, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (49, 'Esel', 'Ein störrisches und nicht besonders flinkes Ding. Aber immerhin lässt es sich (zumindest manchmal) reiten und ist zudem noch ausgesprochen günstig. Genau das Richtige für den weniger betuchten Abenteurer.', 'Reittiere', 'a:4:{s:6:"rounds";s:2:"60";s:6:"atkmod";s:4:"1.05";s:11:"minioncount";s:1:"1";s:8:"activate";s:10:"roundstart";}', 5, 10, 0, 0, 1, '', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (50, 'Skelett-Kampfross', 'Einigen Totenbeschwörern ist es endlich gelungen, in Schlachten gefallene Schlachtrösser neu zu beseelen. Zwar sind sie weniger stark als ihre Lebenden Vorgänger, doch erfüllt der Anblick ihrer blanken Knochen so manchen Gegner mit Schrecken.', 'Reittiere', 'a:6:{s:6:"rounds";s:3:"220";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:3:"1.1";s:15:"maxbadguydamage";s:3:"1.1";s:12:"badguyatkmod";s:3:"0.9";s:8:"activate";s:10:"roundstart";}', 50, 1000, 0, 1, 1, '', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (51, 'Riesenspinne', 'Die großen Waldspinnen der östlichen Wälder. Ihr Biss ist nicht sehr giftig, weshalb es einigen Goblinstämmen gelungen ist diese 4-5 Meter großen Kreaturen zu zähmen. Haltet euch gut im Sattel fest, denn diese Biester können auch problemlos an Wänden oder Decken entlang laufen.', 'Reittiere', 'a:6:{s:6:"rounds";s:3:"120";s:6:"atkmod";s:4:"1.05";s:6:"defmod";s:4:"1.15";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:3:"0.9";s:8:"activate";s:10:"roundstart";}', 55, 2500, 0, 2, 1, '', '', '', 0, 0, 0, '', '', '', 10, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (52, 'Reiteidechse', 'Eigentlich ein nachtaktives und eher harmloses Tier von etwa 5 Metern Länge, dass sich von kleineren Säugern  und Amphibien ernährt. Im Kampf sollte man diese ungewöhnlich schnellen Kreaturen allerdings nicht unterschätzen. Ihre mit Saugnäpfen versehenen Füße lassen sie sich auf jedem Untergrund und sogar an Wänden und Decken gleichermaßen behende bewegen.', 'Reittiere', 'a:6:{s:6:"rounds";s:3:"150";s:6:"atkmod";s:4:"1.05";s:6:"defmod";s:3:"1.1";s:11:"minioncount";s:1:"1";s:12:"badguyatkmod";s:4:"0.95";s:8:"activate";s:10:"roundstart";}', 40, 800, 0, 3, 1, '', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (53, 'Stein-Gargoyle', 'Bei dieser Kreatur handelt es sich nicht nur um ein beeindruckendes Stück zwergischer Handwerkskunst, sondern auch um ein Meisterwerk der Beschwörungskunst. Zwar sind sie nicht intelligent und auch nicht besonders flink, aber dafür kommt einer ihrer Schläge schon gut dem Biss eines kleinen Drachen gleich.', 'Magische Wesen', 'a:5:{s:6:"rounds";s:2:"90";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"8";s:15:"maxbadguydamage";s:2:"16";s:8:"activate";s:7:"offense";}', 10, 500, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (54, 'Feuer-Gargoyle', 'Aus einem haselnussgroßen Rubin beschworen, handelt es sich hierbei um eine Kreatur aus reinstem Feuer, die ihre Gegner mit Feuerbällen, Flammenlanzen und anderen hitzigen Spielereien in die Knie zwingt. Aber Vorsicht! Schon so mancher Held hat sich selbst an diesen Biestern mehr als nur die Finger verbrannt.', 'Magische Wesen', 'a:6:{s:6:"rounds";s:3:"100";s:6:"defmod";s:4:"0.95";s:5:"regen";s:2:"-1";s:15:"minbadguydamage";s:2:"15";s:15:"maxbadguydamage";s:2:"20";s:8:"activate";s:7:"offense";}', 10, 500, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (55, 'Drider', 'Eine groteske und hässliche Kreatur, bestehend aus einem nackten Spinnenkörper, der mit Beinen einen Durchmesser von 3-4 Metern erreicht und an dessen Kopfende ein aufgedunsener und unwirklich erscheindender menschlicher Oberkörper aufragt. So abscheulich diese Kreatur aber anzusehen ist, so gefährlich ist sie auch im Kampf, denn sie vermag zwei Waffen in einem harmonischen und präzisen Einklag zu führen.', 'Magische Wesen', 'a:6:{s:6:"rounds";s:3:"150";s:11:"minioncount";s:1:"2";s:15:"minbadguydamage";s:2:"12";s:15:"maxbadguydamage";s:2:"30";s:12:"badguyatkmod";s:3:"0.9";s:8:"activate";s:7:"offense";}', 60, 7500, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 10, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (56, 'Eule', 'Nur etwa 20-30cm messen diese nachtaktiven und eher harmlos erscheinenden Kreaturen. Bleibt abzuwarten was eure Gegner zu ihren ausdauernden und sturen Angriffen sagen werden...', 'Wildtiere', 'a:5:{s:6:"rounds";s:2:"50";s:11:"minioncount";s:1:"2";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:1:"5";s:8:"activate";s:7:"offense";}', 0, 1200, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (57, 'Giftschlange', 'Unscheinbar in grau-braunen Farbtönen gehalten, ist dies doch eine der gefährlichsten Giftschlangen in den Regionen rund um Atrahor. Keine Rüstung ist lückenlos genug, um diese windige, bis zu einem halben Meter lange Kreatur abzuwehren. Ihr Gift selbst verursacht Krämpfe, Übelkeit und vorrübergehende Sehschwäche. Also gebt Acht dass ihr sie nicht reizt.', 'Wildtiere', 'a:5:{s:6:"rounds";s:1:"5";s:6:"defmod";s:3:"0.5";s:12:"badguyatkmod";s:4:"0.25";s:12:"badguydefmod";s:3:"0.5";s:8:"activate";s:10:"roundstart";}', 5, 100, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (58, 'Anaconda', 'Ein wirklich gefährliches Biest, dass wesentlich schneller ist als es den Anschein hat. Leider wird sie meist schon nach dem ersten Opfer träge, was daran liegen mag, dass sie es mit Haut und Haar verschlingt!', 'Wildtiere', 'a:6:{s:6:"rounds";s:2:"15";s:6:"defmod";s:3:"0.9";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"1";s:15:"maxbadguydamage";s:2:"30";s:8:"activate";s:7:"offense";}', 2, 2500, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (59, 'Fuchs', 'Der gemeine Rot- oder Waldfuchs. Klein, listig und flink. Gut zum Übermitteln von Botschaften (Fuchsen), aber, sofern ihr ihn gut behandelt, auch ein treuer Gefährte im Kampf.', 'Wildtiere', 'a:5:{s:6:"rounds";s:2:"60";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"3";s:15:"maxbadguydamage";s:1:"5";s:8:"activate";s:7:"offense";}', 0, 1500, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (60, 'Hyäne', 'Gefangen in den weiten Steppen der dunklen Lande, nördlich von Atrahor. Selbst mit guter Abrichtung sind sie noch immer feige und hinterhältig. Doch gerade diese Hinterhältigkeit macht sie zu so gefährlichen Gegnern und mit ihren starken und kräftigen Kiefer sind nicht zu unterschätzen.', 'Wildtiere', 'a:5:{s:6:"rounds";s:2:"50";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"3";s:15:"maxbadguydamage";s:1:"9";s:8:"activate";s:7:"offense";}', 0, 2000, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 0, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (61, 'Jaguar', 'Der König der Jäger des Waldes. Stark, schnell und sehr beweglich im Kampf. Diese Exemplare hier sind natürlich relativ zahm und aus Beständen eines königlichen Zoos erstanden worden. Die Haustiere großer Wüstenfürsten und Paschas.', 'Wildtiere', 'a:5:{s:6:"rounds";s:3:"100";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"4";s:15:"maxbadguydamage";s:2:"14";s:8:"activate";s:7:"offense";}', 6, 1000, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (62, 'Tiger', 'Auch diese Tiere sind relativ zahm, stammen sie doch aus einem Zirkus. Gut für Personenschutz geeignet, doch liegt das ganz bei euch und der Art der Abrichtung für die ihr euch entscheidet. Ein Raubtier bleiben sie aber in jedem Fall, also sorgt bitte immer für genug "Futter".', 'Wildtiere', 'a:5:{s:6:"rounds";s:3:"100";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:1:"5";s:15:"maxbadguydamage";s:2:"14";s:8:"activate";s:7:"offense";}', 8, 2000, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 5, 1.14);
INSERT INTO `mounts` (`mountid`, `mountname`, `mountdesc`, `mountcategory`, `mountbuff`, `mountcostgems`, `mountcostgold`, `mountactive`, `mountforestfights`, `tavern`, `newday`, `recharge`, `partrecharge`, `mine_canenter`, `mine_candie`, `mine_cansave`, `mine_tethermsg`, `mine_deathmsg`, `mine_savemsg`, `mindk`, `trainingcost`) VALUES (63, 'Braunbär', 'Nicht unbedingt der größte und beeindruckenste unter den Bären des Reiches, doch mit am leichtesten zu zähmen und obwohl sie plump und träge erscheinen, sind sie doch hervorragende und schnelle Sprinter, mit einer Kraft die einem Menschen mit nur einem Hieb jeden Knochen zu brechen vermag.', 'Wildtiere', 'a:5:{s:6:"rounds";s:3:"100";s:11:"minioncount";s:1:"1";s:15:"minbadguydamage";s:2:"10";s:15:"maxbadguydamage";s:2:"14";s:8:"activate";s:7:"offense";}', 12, 5000, 0, 0, 0, '', '', '', 0, 0, 0, '', '', '', 5, 1.14);
