INSERT INTO `runes_extrainfo` (`id`, `name`, `seltenheit`, `hinweis`, `buchstabe`, `ausrichtung`, `tpl_id`) VALUES (11, 'Isa', 138, 'Zuviel Ruhe und Klarheit führen zu Verhärtung', 'I', 'Stillstand', 'r_isa');
INSERT INTO `runes_extrainfo` (`id`, `name`, `seltenheit`, `hinweis`, `buchstabe`, `ausrichtung`, `tpl_id`) VALUES (12, 'Jera', 147, 'Es ist die Zeit der Ernte ? Zeit der Fülle und Freude, aber auch der harten Arbeit!', 'J', 'Ernte', 'r_jera');
INSERT INTO `runes_extrainfo` (`id`, `name`, `seltenheit`, `hinweis`, `buchstabe`, `ausrichtung`, `tpl_id`) VALUES (13, 'Eiwaz', 156, 'Gibt man sich seiner Angst hin, können Zeiten der Veränderung sich verzögern oder sehr schmerzhaft werden.', 'E', 'Transformation', 'r_eiwaz');
