INSERT INTO `runes_extrainfo` (`id`, `name`, `seltenheit`, `hinweis`, `buchstabe`, `ausrichtung`, `tpl_id`) VALUES (18, 'Berkana', 202, 'Es ist die Zeit der Aussaat ? bis zur Ernte ist es noch lang!', 'B', 'Neubeginn', 'r_berkana');
INSERT INTO `runes_extrainfo` (`id`, `name`, `seltenheit`, `hinweis`, `buchstabe`, `ausrichtung`, `tpl_id`) VALUES (19, 'Ehwaz', 211, 'Soll Energie gebündelt eingesetzt werden, müssen wir besonders achtsam sein, um ein ''Ausbrechen'' der Kräfte zu vermeiden.', 'E', 'Fortschritt', 'r_ehwaz');
INSERT INTO `runes_extrainfo` (`id`, `name`, `seltenheit`, `hinweis`, `buchstabe`, `ausrichtung`, `tpl_id`) VALUES (20, 'Mannaz', 220, 'Befindet man sich in einem Zustand der Ausgeglichenheit, bedeutet das noch lange nicht, dass man auch weniger Achtsam sein darf!', 'M', 'Lebenslauf', 'r_mannaz');
INSERT INTO `runes_extrainfo` (`id`, `name`, `seltenheit`, `hinweis`, `buchstabe`, `ausrichtung`, `tpl_id`) VALUES (21, 'Laguz', 229, 'Nur wenn wir ehrlich mit unseren Gefühlen umgehen, können wir wahrhaftig dem Leben begegnen.', 'L', 'Einklang mit der Welt', 'r_laguz');
INSERT INTO `runes_extrainfo` (`id`, `name`, `seltenheit`, `hinweis`, `buchstabe`, `ausrichtung`, `tpl_id`) VALUES (22, 'Ingwaz', 238, 'Wenn etwas in Ruhe reifen soll, muss man selbst die entsprechende Ruhe bis zur Reife beibehalten.', 'NG', 'die innere Kraft', 'r_ingwaz');
INSERT INTO `runes_extrainfo` (`id`, `name`, `seltenheit`, `hinweis`, `buchstabe`, `ausrichtung`, `tpl_id`) VALUES (23, 'Dagaz', 243, 'Eins bedingt das andere, auch wenn die Dinge sich verändern - so sind sie von ihrem Ursprung her doch eins.', 'D', 'Licht', 'r_dagaz');
INSERT INTO `runes_extrainfo` (`id`, `name`, `seltenheit`, `hinweis`, `buchstabe`, `ausrichtung`, `tpl_id`) VALUES (24, 'Othala', 250, 'Zuhause bedeutet Sicherheit, doch nichts ist wirklich sicher. Zuhause bedeutet auch Arbeit, Auseinandersetzung und Aufrechterhalten.', 'O', 'Konzentration', 'r_othala');
